using System;
using System.Collections.Generic;
using System.Net;
using System.Net.Http;
using System.Text;
using System.Threading.Tasks;
using Newtonsoft.Json;
using Newtonsoft.Json.Linq;

namespace CSharpSingleThreaded.States
{
	public class RegistrationState : IState
	{
		private JobRunnerStateMachine _jobRunnerStateMachine;

		public async Task Handle(JobRunnerStateMachine jobRunnerStateMachine)
		{
			Console.WriteLine("Registering worker");

			_jobRunnerStateMachine = jobRunnerStateMachine;
			try
			{
				var requestUri = $"{Program.UrlBase}{Consts.RegisterAddressSuffix}";
				using (var client = new HttpClient())
				using (var response = await client.PostAsync(requestUri, new StringContent(
					JsonConvert.SerializeObject(new RegistrationModel()),
					Encoding.UTF8,
					"application/json")))
				{
					await HandleResponse(response);
				}
			}
			catch (Exception e)
			{
				jobRunnerStateMachine.State = new FailedState(e.Message);
			}
		}

		private async Task HandleResponse(HttpResponseMessage httpResponseMessage)
		{
			using (var content = httpResponseMessage.Content)
			{
				var result = await content.ReadAsStringAsync();
				if (httpResponseMessage.StatusCode == HttpStatusCode.InternalServerError)
				{
					_jobRunnerStateMachine.State = new FailedState(result);
					return;
				}

				if (Guid.TryParse(result.Replace("\"", ""), out var workerId))
				{
					_jobRunnerStateMachine.WorkerId = workerId;
					_jobRunnerStateMachine.State = new GetJobsState();
				}
				else
				{
					_jobRunnerStateMachine.State = new FailedState(result);
				}
			}
		}
	}

	public class RegistrationModel
	{
		public string Name => "Single threaded counter";
		public string Description => "Single threaded example worker";
		public string Base64Logo => "data:image/png;base64,";
		public List<JobDescription> JobDescriptions => new List<JobDescription>
		{
			new JobDescription
			{
				Name = "Count",
				Description = new List<string>
				{
					"Counts in one second intervals. Params:",
					" - from [int]",
					" - to [int]"
				},
				DefaultPayload = JObject.Parse("{\"from\":0,\"to\":5}")
			}
		};
	}

	public class JobDescription
	{
		public string Name { get; set; }
		public List<string> Description { get; set; }
		public JObject DefaultPayload { get; set; }
	}
}
